				if (!$this->aMembreRelatedByMisAJourPar->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aMembreRelatedByMisAJourPar->getValidationFailures());
				}
			}


			if (($retval = ComptePeer::doValidate($this, $columns)) !== true) {
				$failureMap = array_merge($failureMap, $retval);
			}


				if ($this->collCotisations !== null) {
					foreach ($this->collCotisations as $referrerFK) {
						if (!$referrerFK->validate($columns)) {
							$failureMap = array_merge($failureMap, $referrerFK->getValidationFailures());
						}
					}
				}

				if ($this->collDepenses !== null) {
					foreach ($this->collDepenses as $referrerFK) {
						if (!$referrerFK->validate($columns)) {
							$failureMap = array_merge($failureMap, $referrerFK->getValidationFailures());
						}
					}
				}

				if ($this->collRecettes !== null) {
					foreach ($this->collRecettes as $referrerFK) {
						if (!$referrerFK->validate($columns)) {
							$failureMap = array_merge($failureMap, $referrerFK->getValidationFailures());
