	public function countRecettes(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(ComptePeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collRecettes === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(RecettePeer::COMPTE_ID, $this->id);

				$count = RecettePeer::doCount($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(RecettePeer::COMPTE_ID, $this->id);

				if (!isset($this->lastRecetteCriteria) || !$this->lastRecetteCriteria->equals($criteria)) {
					$count = RecettePeer::doCount($criteria, $con);
				} else {
					$count = count($this->collRecettes);
				}
			} else {
				$count = count($this->collRecettes);
			}
		}
		$this->lastRecetteCriteria = $criteria;
		return $count;
	}

	/**
	 * Method called to associate a Recette object to this object
	 * through the Recette foreign key attribute.
	 *
	 * @param      Recette $l Recette
	 * @return     void
	 * @throws     PropelException
	 */
	public function addRecette(Recette $l)
	{
		if ($this->collRecettes === null) {
			$this->initRecettes();
		}
		if (!in_array($l, $this->collRecettes, true)) { // only add it if the **same** object is not already associated
			array_push($this->collRecettes, $l);
			$l->setCompte($this);
		}
	}


