	/**
	 * Performs the work of inserting or updating the row in the database.
	 *
	 * If the object is new, it inserts it; otherwise an update is performed.
	 * All related objects are also updated in this method.
	 *
	 * @param      PropelPDO $con
	 * @return     int The number of rows affected by this insert/update and any referring fk objects' save() operations.
	 * @throws     PropelException
	 * @see        save()
	 */
	protected function doSave(PropelPDO $con)
	{
		$affectedRows = 0; // initialize var to track total num of affected rows
		if (!$this->alreadyInSave) {
			$this->alreadyInSave = true;

			// We call the save method on the following object(s) if they
			// were passed to this object by their coresponding set
			// method.  This object relates to these object(s) by a
			// foreign key reference.

			if ($this->aCompte !== null) {
				if ($this->aCompte->isModified() || $this->aCompte->isNew()) {
					$affectedRows += $this->aCompte->save($con);
				}
				$this->setCompte($this->aCompte);
			}

			if ($this->aCotisationType !== null) {
				if ($this->aCotisationType->isModified() || $this->aCotisationType->isNew()) {
					$affectedRows += $this->aCotisationType->save($con);
				}
				$this->setCotisationType($this->aCotisationType);
			}

			if ($this->aMembreRelatedByMembreId !== null) {
				if ($this->aMembreRelatedByMembreId->isModified() || $this->aMembreRelatedByMembreId->isNew()) {
					$affectedRows += $this->aMembreRelatedByMembreId->save($con);
				}
				$this->setMembreRelatedByMembreId($this->aMembreRelatedByMembreId);
			}

			if ($this->aMembreRelatedByEnregistrePar !== null) {
				if ($this->aMembreRelatedByEnregistrePar->isModified() || $this->aMembreRelatedByEnregistrePar->isNew()) {
					$affectedRows += $this->aMembreRelatedByEnregistrePar->save($con);
				}
				$this->setMembreRelatedByEnregistrePar($this->aMembreRelatedByEnregistrePar);
			}

			if ($this->aMembreRelatedByMisAJourPar !== null) {
				if ($this->aMembreRelatedByMisAJourPar->isModified() || $this->aMembreRelatedByMisAJourPar->isNew()) {
					$affectedRows += $this->aMembreRelatedByMisAJourPar->save($con);
				}
				$this->setMembreRelatedByMisAJourPar($this->aMembreRelatedByMisAJourPar);
			}

			if ($this->isNew() ) {
				$this->modifiedColumns[] = CotisationPeer::ID;
			}

			// If this object has been modified, then save it to the database.
			if ($this->isModified()) {
				if ($this->isNew()) {
					$pk = CotisationPeer::doInsert($this, $con);
					$affectedRows += 1; // we are assuming that there is only 1 row per doInsert() which
										 // should always be true here (even though technically
										 // BasePeer::doInsert() can insert multiple rows).

					$this->setId($pk);  //[IMV] update autoincrement primary key

					$this->setNew(false);
				} else {
					$affectedRows += CotisationPeer::doUpdate($this, $con);
				}

				$this->resetModified(); // [HL] After being saved an object is no longer 'modified'
			}

			$this->alreadyInSave = false;

		}
		return $affectedRows;
	} // doSave()

