	public function buildCriteria()
	{
		$criteria = new Criteria(CotisationPeer::DATABASE_NAME);

		if ($this->isColumnModified(CotisationPeer::ID)) $criteria->add(CotisationPeer::ID, $this->id);
		if ($this->isColumnModified(CotisationPeer::COMPTE_ID)) $criteria->add(CotisationPeer::COMPTE_ID, $this->compte_id);
		if ($this->isColumnModified(CotisationPeer::COTISATION_TYPE_ID)) $criteria->add(CotisationPeer::COTISATION_TYPE_ID, $this->cotisation_type_id);
		if ($this->isColumnModified(CotisationPeer::MEMBRE_ID)) $criteria->add(CotisationPeer::MEMBRE_ID, $this->membre_id);
		if ($this->isColumnModified(CotisationPeer::DATE)) $criteria->add(CotisationPeer::DATE, $this->date);
		if ($this->isColumnModified(CotisationPeer::ENREGISTRE_PAR)) $criteria->add(CotisationPeer::ENREGISTRE_PAR, $this->enregistre_par);
		if ($this->isColumnModified(CotisationPeer::MONTANT)) $criteria->add(CotisationPeer::MONTANT, $this->montant);
		if ($this->isColumnModified(CotisationPeer::MIS_A_JOUR_PAR)) $criteria->add(CotisationPeer::MIS_A_JOUR_PAR, $this->mis_a_jour_par);
		if ($this->isColumnModified(CotisationPeer::CREATED_AT)) $criteria->add(CotisationPeer::CREATED_AT, $this->created_at);
		if ($this->isColumnModified(CotisationPeer::UPDATED_AT)) $criteria->add(CotisationPeer::UPDATED_AT, $this->updated_at);

		return $criteria;
	}

