		}

		AssociationPeer::addSelectColumns($c);
		$startcol2 = (AssociationPeer::NUM_COLUMNS - AssociationPeer::NUM_LAZY_LOAD_COLUMNS);

		MembrePeer::addSelectColumns($c);
		$startcol3 = $startcol2 + (MembrePeer::NUM_COLUMNS - MembrePeer::NUM_LAZY_LOAD_COLUMNS);

