				$obj1->hydrate($row);
				AssociationPeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

			// Add objects for joined Membre rows

			$key2 = MembrePeer::getPrimaryKeyHashFromRow($row, $startcol2);
			if ($key2 !== null) {
				$obj2 = MembrePeer::getInstanceFromPool($key2);
				if (!$obj2) {

					$omClass = MembrePeer::getOMClass();


					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					MembrePeer::addInstanceToPool($obj2, $key2);
				} // if obj2 loaded

				// Add the $obj1 (Association) to the collection in $obj2 (Membre)
				$obj2->addAssociationRelatedByEnregistrePar($obj1);
			} // if joined row not null

			// Add objects for joined Membre rows

			$key3 = MembrePeer::getPrimaryKeyHashFromRow($row, $startcol3);
			if ($key3 !== null) {
				$obj3 = MembrePeer::getInstanceFromPool($key3);
				if (!$obj3) {

					$omClass = MembrePeer::getOMClass();


					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj3 = new $cls();
					$obj3->hydrate($row, $startcol3);
					MembrePeer::addInstanceToPool($obj3, $key3);
				} // if obj3 loaded

				// Add the $obj1 (Association) to the collection in $obj3 (Membre)
				$obj3->addAssociationRelatedByMisAJourPar($obj1);
			} // if joined row not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}


