	/**
	 * Method perform an INSERT on the database, given a Association or Criteria object.
	 *
	 * @param      mixed $values Criteria or Association object containing data that is used to create the INSERT statement.
	 * @param      PropelPDO $con the PropelPDO connection to use
	 * @return     mixed The new primary key.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doInsert($values, PropelPDO $con = null)
	{

    foreach (sfMixer::getCallables('BaseAssociationPeer:doInsert:pre') as $callable)
    {
      $ret = call_user_func($callable, 'BaseAssociationPeer', $values, $con);
      if (false !== $ret)
      {
        return $ret;
      }
    }


		if ($con === null) {
			$con = Propel::getConnection(AssociationPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from Association object
		}

		if ($criteria->containsKey(AssociationPeer::ID) && $criteria->keyContainsValue(AssociationPeer::ID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.AssociationPeer::ID.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

		
    foreach (sfMixer::getCallables('BaseAssociationPeer:doInsert:post') as $callable)
    {
      call_user_func($callable, 'BaseAssociationPeer', $values, $con, $pk);
    }

    return $pk;
	}

	/**
	 * Method perform an UPDATE on the database, given a Association or Criteria object.
	 *
	 * @param      mixed $values Criteria or Association object containing data that is used to create the UPDATE statement.
	 * @param      PropelPDO $con The connection to use (specify PropelPDO connection object to exert more control over transactions).
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doUpdate($values, PropelPDO $con = null)
	{

    foreach (sfMixer::getCallables('BaseAssociationPeer:doUpdate:pre') as $callable)
    {
      $ret = call_user_func($callable, 'BaseAssociationPeer', $values, $con);
      if (false !== $ret)
      {
        return $ret;
      }
    }


		if ($con === null) {
			$con = Propel::getConnection(AssociationPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		$selectCriteria = new Criteria(self::DATABASE_NAME);

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity

			$comparison = $criteria->getComparison(AssociationPeer::ID);
			$selectCriteria->add(AssociationPeer::ID, $criteria->remove(AssociationPeer::ID), $comparison);

		} else { // $values is Association object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

		// set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$ret = BasePeer::doUpdate($selectCriteria, $criteria, $con);
	

    foreach (sfMixer::getCallables('BaseAssociationPeer:doUpdate:post') as $callable)
    {
      call_user_func($callable, 'BaseAssociationPeer', $values, $con, $ret);
    }

    return $ret;
  }

	/**
	 * Method to DELETE all rows from the association table.
	 *
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	public static function doDeleteAll($con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(AssociationPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}
		$affectedRows = 0; // initialize var to track total num of affected rows
		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += AssociationPeer::doOnDeleteCascade(new Criteria(AssociationPeer::DATABASE_NAME), $con);
			$affectedRows += BasePeer::doDeleteAll(AssociationPeer::TABLE_NAME, $con);
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * Method perform a DELETE on the database, given a Association or Criteria object OR a primary key value.
	 *
	 * @param      mixed $values Criteria or Association object or primary key or array of primary keys
	 *              which is used to create the DELETE statement
	 * @param      PropelPDO $con the connection to use
	 * @return     int 	The number of affected rows (if supported by underlying database driver).  This includes CASCADE-related rows
	 *				if supported by native driver or if emulated using Propel.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	 public static function doDelete($values, PropelPDO $con = null)
	 {
		if ($con === null) {
			$con = Propel::getConnection(AssociationPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			// invalidate the cache for all objects of this type, since we have no
			// way of knowing (without running a query) what objects should be invalidated
			// from the cache based on this Criteria.
			AssociationPeer::clearInstancePool();

			// rename for clarity
			$criteria = clone $values;
		} elseif ($values instanceof Association) {
			// invalidate the cache for this single object
			AssociationPeer::removeInstanceFromPool($values);
			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else {
			// it must be the primary key



			$criteria = new Criteria(self::DATABASE_NAME);
			$criteria->add(AssociationPeer::ID, (array) $values, Criteria::IN);

			foreach ((array) $values as $singleval) {
				// we can invalidate the cache for this single object
				AssociationPeer::removeInstanceFromPool($singleval);
			}
		}

		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$affectedRows = 0; // initialize var to track total num of affected rows

		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += AssociationPeer::doOnDeleteCascade($criteria, $con);
			
				// Because this db requires some delete cascade/set null emulation, we have to
				// clear the cached instance *after* the emulation has happened (since
				// instances get re-added by the select statement contained therein).
				if ($values instanceof Criteria) {
					AssociationPeer::clearInstancePool();
				} else { // it's a PK or object
					AssociationPeer::removeInstanceFromPool($values);
				}
			
			$affectedRows += BasePeer::doDelete($criteria, $con);

			// invalidate objects in ActivitePeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			ActivitePeer::clearInstancePool();

			// invalidate objects in ComptePeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			ComptePeer::clearInstancePool();

			// invalidate objects in CotisationTypePeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			CotisationTypePeer::clearInstancePool();

			// invalidate objects in DepensePeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			DepensePeer::clearInstancePool();

			// invalidate objects in MembrePeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			MembrePeer::clearInstancePool();

			// invalidate objects in RecettePeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			RecettePeer::clearInstancePool();

			// invalidate objects in StatutPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			StatutPeer::clearInstancePool();

			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * This is a method for emulating ON DELETE CASCADE for DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	protected static function doOnDeleteCascade(Criteria $criteria, PropelPDO $con)
	{
		// initialize var to track total num of affected rows
		$affectedRows = 0;

		// first find the objects that are implicated by the $criteria
		$objects = AssociationPeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {


			// delete related Activite objects
			$c = new Criteria(ActivitePeer::DATABASE_NAME);
			
			$c->add(ActivitePeer::ASSOCIATION_ID, $obj->getId());
			$affectedRows += ActivitePeer::doDelete($c, $con);

			// delete related Compte objects
			$c = new Criteria(ComptePeer::DATABASE_NAME);
			
			$c->add(ComptePeer::ASSOCIATION_ID, $obj->getId());
			$affectedRows += ComptePeer::doDelete($c, $con);

			// delete related CotisationType objects
			$c = new Criteria(CotisationTypePeer::DATABASE_NAME);
			
			$c->add(CotisationTypePeer::ASSOCIATION_ID, $obj->getId());
			$affectedRows += CotisationTypePeer::doDelete($c, $con);

			// delete related Depense objects
			$c = new Criteria(DepensePeer::DATABASE_NAME);
			
			$c->add(DepensePeer::ASSOCIATION_ID, $obj->getId());
			$affectedRows += DepensePeer::doDelete($c, $con);

			// delete related Membre objects
			$c = new Criteria(MembrePeer::DATABASE_NAME);
			
			$c->add(MembrePeer::ASSOCIATION_ID, $obj->getId());
			$affectedRows += MembrePeer::doDelete($c, $con);

			// delete related Recette objects
			$c = new Criteria(RecettePeer::DATABASE_NAME);
			
			$c->add(RecettePeer::ASSOCIATION_ID, $obj->getId());
			$affectedRows += RecettePeer::doDelete($c, $con);

			// delete related Statut objects
			$c = new Criteria(StatutPeer::DATABASE_NAME);
			
			$c->add(StatutPeer::ASSOCIATION_ID, $obj->getId());
			$affectedRows += StatutPeer::doDelete($c, $con);
		}
		return $affectedRows;
	}

	/**
	 * Validates all modified columns of given Association object.
	 * If parameter $columns is either a single column name or an array of column names
	 * than only those columns are validated.
	 *
	 * NOTICE: This does not apply to primary or foreign keys for now.
	 *
	 * @param      Association $obj The object to validate.
	 * @param      mixed $cols Column name or array of column names.
	 *
	 * @return     mixed TRUE if all columns are valid or the error message of the first invalid column.
	 */
	public static function doValidate(Association $obj, $cols = null)
	{
		$columns = array();

		if ($cols) {
			$dbMap = Propel::getDatabaseMap(AssociationPeer::DATABASE_NAME);
			$tableMap = $dbMap->getTable(AssociationPeer::TABLE_NAME);

			if (! is_array($cols)) {
				$cols = array($cols);
			}

			foreach ($cols as $colName) {
				if ($tableMap->containsColumn($colName)) {
					$get = 'get' . $tableMap->getColumn($colName)->getPhpName();
					$columns[$colName] = $obj->$get();
				}
			}
		} else {

		}

		$res =  BasePeer::doValidate(AssociationPeer::DATABASE_NAME, AssociationPeer::TABLE_NAME, $columns);
    if ($res !== true) {
        $request = sfContext::getInstance()->getRequest();
        foreach ($res as $failed) {
            $col = AssociationPeer::translateFieldname($failed->getColumn(), BasePeer::TYPE_COLNAME, BasePeer::TYPE_PHPNAME);
        }
    }

    return $res;
	}

	/**
	 * Retrieve a single object by pkey.
	 *
	 * @param      int $pk the primary key.
	 * @param      PropelPDO $con the connection to use
	 * @return     Association
	 */
	public static function retrieveByPK($pk, PropelPDO $con = null)
	{

		if (null !== ($obj = AssociationPeer::getInstanceFromPool((string) $pk))) {
			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(AssociationPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$criteria = new Criteria(AssociationPeer::DATABASE_NAME);
		$criteria->add(AssociationPeer::ID, $pk);

		$v = AssociationPeer::doSelect($criteria, $con);

		return !empty($v) > 0 ? $v[0] : null;
	}

	/**
	 * Retrieve multiple objects by pkey.
	 *
	 * @param      array $pks List of primary keys
	 * @param      PropelPDO $con the connection to use
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function retrieveByPKs($pks, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(AssociationPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$objs = null;
		if (empty($pks)) {
			$objs = array();
		} else {
			$criteria = new Criteria(AssociationPeer::DATABASE_NAME);
			$criteria->add(AssociationPeer::ID, $pks, Criteria::IN);
			$objs = AssociationPeer::doSelect($criteria, $con);
		}
		return $objs;
	}

